mapper.update:
update dlxy_article set update_date=#{updateDate} ,


		article_status=#{status}

			,delete_date =#{deleteDate}



	article_type=#{type}


where 1=1

	and exists(select 1 from dlxy_title where title_id
	=#{titleId}

and article_id in

	#{id}

mapper.select:
select count(1) from dlxy_article where 1=1 and
title_id in

	#{id}

select a.article_id,a.title_id,a.article_name,a.article_author,a.article_type,a.create_date,a.update_date,a.article_status ,c.realname,c.user_id,a.delete_date,b.title_name from dlxy_article a left join dlxy_title b on a.title_id=b.title_id left join dlxy_user_article c on a.article_id=c.article_id where 1=1 and a.title_id in
#{id}
order by a.create_date desc limit #{start},#{end}
